import rs.gospaleks.waterspot.presentation.components.CleanlinessChip
import rs.gospaleks.waterspot.R

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SpotDetailsBottomSheet(
    spotDetails: SpotDetails? = null,
    isLoading: Boolean,
    selectedSpotId: String? = null,
    onDismiss: () -> Unit,
    onReviewClick: () -> Unit,
    onNavigateClick: () -> Unit,
    onLoadSpotDetails: (String) -> Unit = {},
    onUserProfileClick: () -> Unit = {}
) {
    LaunchedEffect(selectedSpotId) {
        selectedSpotId?.let { spotId ->
            // Mala pauza da se animacija otvaranja završi
            delay(200)
            onLoadSpotDetails(spotId)
        }
    }

    ModalBottomSheet(
        onDismissRequest = onDismiss,
    ) {
        Column(
            modifier = Modifier.fillMaxWidth()
        ) {
            if (isLoading || spotDetails == null) {
                Box(
                    modifier = Modifier
                        .fillMaxHeight(0.5f)
                        .fillMaxWidth(),
                    contentAlignment = Alignment.Center,
                ) {
                    CircularProgressIndicator()
                }
            } else {
                SpotDetailsContent(
                    spotDetails = spotDetails,
                    onNavigateClick = onNavigateClick,
                    onReviewClick = onReviewClick,
                    onUserProfileClick = onUserProfileClick
                )
            }
        }
    }
}

@Composable
fun SpotDetailsContent(
    spotDetails: SpotDetails,
    onReviewClick: () -> Unit = {},
    onNavigateClick: () -> Unit = {},
    onUserProfileClick: () -> Unit = {}
) {
    Column(
        modifier = Modifier
            .fillMaxWidth()
            .padding(16.dp)
    ) {
        // Photo
        spotDetails.spot.photoUrl?.let { photoUrl ->
            AsyncImage(
                model = photoUrl,
                contentDescription = null,
                modifier = Modifier
                    .fillMaxWidth()
                    .aspectRatio(4f / 3f)
                    .clip(RoundedCornerShape(16.dp)),
                contentScale = ContentScale.Crop
            )

            Spacer(modifier = Modifier.height(16.dp))
        }

        // Header with type and date
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            // Type with icon
            Row(
                verticalAlignment = Alignment.CenterVertically,
                modifier = Modifier.weight(1f)
            ) {
                Icon(
                    imageVector = spotDetails.spot.type.icon(),
                    contentDescription = null,
                    tint = MaterialTheme.colorScheme.primary,
                    modifier = Modifier.size(24.dp)
                )
                Spacer(modifier = Modifier.width(8.dp))
                Text(
                    text = spotDetails.spot.type.toDisplayName(),
                    style = MaterialTheme.typography.headlineSmall,
                    color = MaterialTheme.colorScheme.onSurface
                )
            }

            // Cleanliness chip
            CleanlinessChip(
                cleanliness = spotDetails.spot.cleanliness,
                modifier = Modifier.wrapContentWidth()
            )
        }

        // Description
        spotDetails.spot.description?.let { description ->
            Spacer(modifier = Modifier.height(12.dp))

            Text(
                text = description,
                modifier = Modifier.fillMaxWidth().padding(16.dp),
                style = MaterialTheme.typography.bodyMedium,
                color = MaterialTheme.colorScheme.onSurface
            )
        }

        // Clickable user info
        spotDetails.user?.let { user ->
            PostedByCard(user, onUserProfileClick)
        }

        Spacer(modifier = Modifier.height(20.dp))

        // Action buttons
        ActionsButtons(
            onNavigateClick = onNavigateClick,
            onReviewClick = onReviewClick
        )
    }
}

@Composable
fun PostedByCard(
    user: User,
    onUserProfileClick: () -> Unit,
) {
    Spacer(modifier = Modifier.height(16.dp))

    Card(
        onClick = onUserProfileClick,
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surfaceContainer
        ),
        modifier = Modifier.fillMaxWidth()
    ) {
        Row(
            verticalAlignment = Alignment.CenterVertically,
            modifier = Modifier.padding(12.dp)
        ) {
            AsyncImage(
                model = user.profilePictureUrl,
                contentDescription = "Profile picture",
                modifier = Modifier
                    .size(48.dp)
                    .clip(CircleShape),
                contentScale = ContentScale.Crop
            )
            Spacer(modifier = Modifier.width(12.dp))
            Column {
                Text(
                    text = stringResource(R.string.spot_details_posted_by),
                    style = MaterialTheme.typography.labelSmall,
                    color = MaterialTheme.colorScheme.onSurfaceVariant
                )
                Text(
                    text = user.fullName,
                    style = MaterialTheme.typography.bodyMedium,
                    color = MaterialTheme.colorScheme.onSurface
                )
            }
            Spacer(modifier = Modifier.weight(1f))
            Icon(
                imageVector = Icons.AutoMirrored.Filled.ArrowForward,
                contentDescription = null,
                tint = MaterialTheme.colorScheme.onSurfaceVariant,
                modifier = Modifier.size(20.dp)
            )
        }
    }
}

@Composable
fun ActionsButtons(
    onNavigateClick: () -> Unit,
    onReviewClick: () -> Unit,
) {
    Row(
        horizontalArrangement = Arrangement.spacedBy(12.dp),
        modifier = Modifier.fillMaxWidth()
    ) {
        Button(
            onClick = onNavigateClick,
            modifier = Modifier.weight(1f),
            contentPadding = PaddingValues(vertical = 12.dp)
        ) {
            Icon(
                imageVector = Icons.Default.Navigation,
                contentDescription = null,
                modifier = Modifier.size(18.dp)
            )
            Spacer(modifier = Modifier.width(8.dp))
            Text(text = stringResource(R.string.spot_details_navigate_button))
        }

        OutlinedButton(
            onClick = onReviewClick,
            modifier = Modifier.weight(1f),
            contentPadding = PaddingValues(vertical = 12.dp)
        ) {
            Icon(
                imageVector = Icons.Outlined.Report,
                contentDescription = null,
                modifier = Modifier.size(18.dp)
            )
            Spacer(modifier = Modifier.width(8.dp))
            Text(text = stringResource(R.string.spot_details_review_button))
        }
    }
}

