@Composable
fun EditProfileScreen(
    onBackClick: () -> Unit,
    viewModel: EditProfileViewModel = hiltViewModel()
) {
    val context = LocalContext.current
    val uiState = viewModel.uiState

    LaunchedEffect(viewModel) {
        viewModel.eventFlow.collect { event ->
            if (event is UiEvent.ShowToast) {
                Toast.makeText(context, event.message, Toast.LENGTH_SHORT).show()
            }
        }
    }

    Scaffold(
        topBar = {
            BasicTopAppBar(
                title = stringResource(id = R.string.edit_profile_title),
                onBackClick = onBackClick
            )
        },
        contentWindowInsets = WindowInsets(0.dp, 0.dp, 0.dp, dimensionResource(R.dimen.padding_extra_large))
    ) { innerPadding ->

    }
}

package rs.gospaleks.waterspot.presentation.screens.profile.edit
import android.widget.Toast import androidx.compose.foundation.background import androidx.compose.foundation.layout.* import androidx.compose.foundation.rememberScrollState import androidx.compose.foundation.shape.CircleShape import androidx.compose.foundation.shape.RoundedCornerShape import androidx.compose.foundation.verticalScroll import androidx.compose.material.icons.Icons import androidx.compose.material.icons.filled.Email import androidx.compose.material.icons.filled.Person import androidx.compose.material.icons.filled.Phone import androidx.compose.material3.* import androidx.compose.runtime.Composable import androidx.compose.runtime.LaunchedEffect import androidx.compose.ui.Alignment import androidx.compose.ui.Modifier import androidx.compose.ui.platform.LocalContext import androidx.compose.ui.res.dimensionResource import androidx.compose.ui.res.stringResource import androidx.compose.ui.unit.dp import androidx.hilt.navigation.compose.hiltViewModel import rs.gospaleks.waterspot.R import rs.gospaleks.waterspot.presentation.components.BasicTopAppBar import rs.gospaleks.waterspot.presentation.components.UiEvent import androidx.compose.foundation.text.KeyboardOptions
@Composable fun EditProfileScreen( onBackClick: () -> Unit, viewModel: EditProfileViewModel = hiltViewModel() ) { val context = LocalContext.current val uiState = viewModel.uiState
LaunchedEffect(viewModel) { viewModel.eventFlow.collect { event -> if (event is UiEvent.ShowToast) { Toast.makeText(context, event.message, Toast.LENGTH_SHORT).show() } } }
Scaffold( topBar = { BasicTopAppBar( title = stringResource(id = R.string.edit_profile_title), onBackClick = onBackClick ) }, contentWindowInsets = WindowInsets(0.dp, 0.dp, 0.dp, dimensionResource(R.dimen.padding_extra_large)) ) { innerPadding -> // Treat blank state + loading as initial loading; otherwise use in-button loader val initialLoading = uiState.isLoading && uiState.email.isBlank() && uiState.fullName.isBlank() && uiState.phoneNumber.isBlank()
if (initialLoading) { Box( modifier = Modifier .padding(innerPadding) .fillMaxSize(), contentAlignment = Alignment.Center ) { CircularProgressIndicator() } } else { Column( modifier = Modifier .padding(innerPadding) .consumeWindowInsets(innerPadding) .padding(horizontal = dimensionResource(R.dimen.padding_extra_large)) .fillMaxSize() .verticalScroll(rememberScrollState()) .imePadding(), horizontalAlignment = Alignment.CenterHorizontally ) { // Decorative header icon Box( modifier = Modifier .size(96.dp) .padding(top = 24.dp) .background( color = MaterialTheme.colorScheme.primary.copy(alpha = 0.08f), shape = CircleShape ), contentAlignment = Alignment.Center ) { Icon( imageVector = Icons.Default.Person, contentDescription = stringResource(R.string.edit_profile_header_icon_cd), tint = MaterialTheme.colorScheme.primary, modifier = Modifier.size(40.dp) ) }
Spacer(modifier = Modifier.height(24.dp))
// Email (read-only) OutlinedTextField( value = uiState.email, onValueChange = {}, label = { Text(stringResource(R.string.email_label)) }, leadingIcon = { Icon(Icons.Default.Email, contentDescription = null) }, modifier = Modifier.fillMaxWidth(), singleLine = true, enabled = false )
Spacer(modifier = Modifier.height(12.dp))
// Full name OutlinedTextField( value = uiState.fullName, onValueChange = viewModel::onFullNameChange, label = { Text(stringResource(R.string.full_name_label)) }, leadingIcon = { Icon(Icons.Default.Person, contentDescription = null) }, modifier = Modifier.fillMaxWidth(), singleLine = true, enabled = !uiState.isLoading )
Spacer(modifier = Modifier.height(12.dp))
// Phone number OutlinedTextField( value = uiState.phoneNumber, onValueChange = viewModel::onPhoneNumberChange, label = { Text(stringResource(R.string.phone_number_label)) }, leadingIcon = { Icon(Icons.Default.Phone, contentDescription = null) }, keyboardOptions = KeyboardOptions(keyboardType = androidx.compose.ui.text.input.KeyboardType.Phone), modifier = Modifier.fillMaxWidth(), singleLine = true, enabled = !uiState.isLoading )
Spacer(modifier = Modifier.weight(1f))
val buttonEnabled = !uiState.isLoading && uiState.fullName.isNotBlank() && uiState.phoneNumber.isNotBlank()
Button( onClick = { viewModel.onSaveChanges() }, modifier = Modifier .fillMaxWidth() .height(52.dp), shape = RoundedCornerShape(28.dp), enabled = buttonEnabled ) { if (uiState.isLoading) { CircularProgressIndicator( color = MaterialTheme.colorScheme.onPrimary, strokeWidth = 2.dp, modifier = Modifier.size(20.dp) ) } else { Text( text = stringResource(R.string.save), style = MaterialTheme.typography.titleMedium ) } }
Spacer(modifier = Modifier.height(8.dp)) } } } }
