@Composable
fun AllSpotsScreen(
    outerPadding: PaddingValues,
    viewModel: AllSpotsViewModel = hiltViewModel()
) {
    val bottomSheetViewModel: SpotDetailsBottomSheetViewModel = hiltViewModel()

    val uiState = viewModel.uiState

    Column (
        modifier = Modifier
            .padding(bottom = outerPadding.calculateBottomPadding())
            .fillMaxSize(),
        verticalArrangement = Arrangement.Top,
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        SearchAndFilter(
            textFieldState = viewModel.textFieldState,
            searchResults = uiState.filteredSpots.map { it.spot.type.toDisplayName() },
            selectedTypes = uiState.selectedTypeFilters,
            onToggleType = viewModel::toggleTypeFilter,
            selectedCleanliness = uiState.selectedCleanlinessFilters,
            onToggleCleanliness = viewModel::toggleCleanlinessFilter,
            radiusKm = uiState.radiusKm,
            onRadiusChange = viewModel::updateRadiusKm,
            onRadiusChangeFinished = viewModel::applyRadiusChange,
            onQueryChange = viewModel::setSearchQuery,
        )

        // Content area (list/loading/empty)
        when {
            uiState.isLoading -> {
                // Shimmer skeleton list
                LazyColumn(
                    modifier = Modifier
                        .fillMaxWidth()
                        .weight(1f),
                    verticalArrangement = Arrangement.spacedBy(4.dp),
                    contentPadding = PaddingValues(vertical = 8.dp)
                ) {
                    items(6) { index ->
                        ShimmerSpotCardPlaceholder(modifier = if (index == 0) Modifier.padding(top = 8.dp) else Modifier)
                    }
                }
            }
            uiState.allSpots.isEmpty() -> {
                Box(
                    modifier = Modifier
                        .fillMaxWidth()
                        .weight(1f)
                        .padding(outerPadding),
                    contentAlignment = Alignment.Center
                ) {
                    Column(
                        horizontalAlignment = Alignment.CenterHorizontally,
                        verticalArrangement = Arrangement.Center
                    ) {
                        Icon(
                            imageVector = Icons.Outlined.WaterDrop,
                            contentDescription = null,
                            tint = MaterialTheme.colorScheme.primary,
                            modifier = Modifier.size(48.dp)
                        )
                        Spacer(modifier = Modifier.height(12.dp))
                        Text(
                            text = stringResource(R.string.all_spots_empty_message),
                            style = MaterialTheme.typography.titleMedium,
                            color = MaterialTheme.colorScheme.onSurface
                        )
                    }
                }
            }
            else -> {
                LazyColumn(
                    modifier = Modifier
                        .weight(1f)
                        .padding(horizontal = outerPadding.calculateStartPadding(LayoutDirection.Ltr)),
                    verticalArrangement = Arrangement.spacedBy(4.dp),
                ) {
                    items(
                        items = uiState.filteredSpots,
                        key = { it.spot.id }
                    ) { spotWithUser ->
                        val index = uiState.filteredSpots.indexOf(spotWithUser)
                        val visible = remember(spotWithUser.spot.id) { mutableStateOf(false) }
                        LaunchedEffect(spotWithUser.spot.id) { visible.value = true }

                        AnimatedVisibility(
                            visible = visible.value,
                            enter = fadeIn(animationSpec = tween(180, easing = FastOutSlowInEasing)) +
                                    expandVertically(animationSpec = tween(180, easing = FastOutSlowInEasing)),
                            exit = fadeOut(animationSpec = tween(120, easing = FastOutSlowInEasing)) +
                                    shrinkVertically(animationSpec = tween(120, easing = FastOutSlowInEasing))
                        ) {
                            SpotCard(
                                spotWithUser = spotWithUser,
                                modifier = (if (index == 0) Modifier.padding(top = 8.dp) else Modifier)
                                    .animateContentSize(),
                                onCardClick = {
                                    bottomSheetViewModel.onSpotClick(spotWithUser)
                                },
                                onUserClick = { userId ->
                                    // TODO: Navigiraj do profila korisnika
                                },
                            )
                        }
                    }
                }
            }
        }
    }

    SpotDetailsBottomSheet(viewModel = bottomSheetViewModel)
}

@Composable
private fun rememberShimmerBrush(): Brush {
    val transition = rememberInfiniteTransition(label = "shimmer")
    val translate by transition.animateFloat(
        initialValue = 0f,
        targetValue = 1000f,
        animationSpec = infiniteRepeatable(
            animation = tween(durationMillis = 1000, easing = LinearEasing),
            repeatMode = RepeatMode.Restart
        ),
        label = "shimmerTranslate"
    )
    val base = MaterialTheme.colorScheme.surfaceVariant
    val highlight = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.10f)

    return Brush.linearGradient(
        colors = listOf(base.copy(alpha = 0.6f), highlight, base.copy(alpha = 0.6f)),
        start = Offset(translate - 200f, 0f),
        end = Offset(translate, 0f)
    )
}

@Composable
private fun ShimmerSpotCardPlaceholder(modifier: Modifier = Modifier) {
    val shimmer = rememberShimmerBrush()
    val shape = RoundedCornerShape(16.dp)

    Row(
        modifier = modifier
            .fillMaxWidth()
            .padding(horizontal = 24.dp, vertical = 8.dp)
            .clip(shape)
            .background(MaterialTheme.colorScheme.surfaceContainer)
            .height(110.dp)
            .padding(12.dp)
    ) {
        // Left image placeholder
        Box(
            modifier = Modifier
                .width(110.dp)
                .fillMaxHeight()
                .clip(RoundedCornerShape(12.dp))
                .background(shimmer)
        )

        Spacer(Modifier.width(12.dp))

        // Right content placeholders
        Column(modifier = Modifier.fillMaxHeight().weight(1f)) {
            // Title row placeholder
            Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.SpaceBetween) {
                Box(
                    modifier = Modifier
                        .height(16.dp)
                        .width(120.dp)
                        .clip(RoundedCornerShape(6.dp))
                        .background(shimmer)
                )
                Box(
                    modifier = Modifier
                        .height(16.dp)
                        .width(70.dp)
                        .clip(RoundedCornerShape(6.dp))
                        .background(shimmer)
                )
            }

            Spacer(Modifier.height(12.dp))

            // Second line placeholder
            Box(
                modifier = Modifier
                    .height(12.dp)
                    .fillMaxWidth(0.7f)
                    .clip(RoundedCornerShape(6.dp))
                    .background(shimmer)
            )

            Spacer(modifier = Modifier.weight(1f))

            // Footer row placeholder
            Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.SpaceBetween) {
                Box(
                    modifier = Modifier
                        .height(12.dp)
                        .width(100.dp)
                        .clip(RoundedCornerShape(6.dp))
                        .background(shimmer)
                )
                Box(
                    modifier = Modifier
                        .height(12.dp)
                        .width(60.dp)
                        .clip(RoundedCornerShape(6.dp))
                        .background(shimmer)
                )
            }
        }
    }
}

